import React, { useState, useEffect, useCallback } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables); // Required for Chart.js v3+

// --- Main App Component: Manages global state and page routing ---
function App() {
    const [activePage, setActivePage] = useState('search');
    const [portfolioData, setPortfolioData] = useState({ cash: 0, stocks: [] });
    const [watchlist, setWatchlist] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const API_URL = "http://127.0.0.1:5001";

    // Fetches all necessary data from the backend
    const fetchData = useCallback(async () => {
        setIsLoading(true);
        try {
            const [portfolioRes, watchlistRes] = await Promise.all([
                fetch(`${API_URL}/portfolio`),
                fetch(`${API_URL}/watchlist`)
            ]);
            const portfolio = await portfolioRes.json();
            const watchlistData = await watchlistRes.json();
            setPortfolioData(portfolio);
            setWatchlist(watchlistData);
        } catch (error) { console.error("Failed to fetch initial data:", error); } 
        finally { setIsLoading(false); }
    }, []);

    useEffect(() => { fetchData(); }, [fetchData]);

    // Renders the correct page based on the activePage state
    const renderPage = () => {
        const props = { API_URL, refreshData: fetchData, watchlist, setWatchlist };
        switch (activePage) {
            case 'search': return <SearchPage {...props} />;
            case 'watchlist': return <WatchlistPage {...props} />;
            case 'portfolio': return <PortfolioPage portfolioData={portfolioData} isLoading={isLoading} {...props} />;
            case 'gettingStarted': return <GettingStartedPage />;
            default: return <SearchPage {...props} />;
        }
    };

    return (
        <div className="min-h-screen bg-gray-50 font-sans">
            <style>{`.animate-fade-in { animation: fade-in 0.5s ease-out; } @keyframes fade-in { from { opacity: 0; transform: translateY(10px); } to { opacity: 1; transform: translateY(0); } }`}</style>
            <Header activePage={activePage} setActivePage={setActivePage} />
            <main>{renderPage()}</main>
        </div>
    );
}

// --- Reusable Components ---
const Header = ({ activePage, setActivePage }) => ( 
    <header className="bg-gray-800 text-white shadow-lg sticky top-0 z-50">
        <nav className="container mx-auto px-6 py-4 flex justify-between items-center">
            <div className="flex items-center"> <h1 className="text-xl font-bold">MarketMind</h1> </div>
            <div className="flex items-center space-x-2 bg-gray-900 rounded-lg p-1">
                {['Search', 'Watchlist', 'Portfolio', 'Getting Started'].map(page => (
                    <button key={page} onClick={() => setActivePage(page.toLowerCase().replace(' ', ''))} 
                            className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${activePage === page.toLowerCase().replace(' ', '') ? 'bg-blue-600 text-white' : 'text-gray-300 hover:bg-gray-700'}`}>
                        {page}
                    </button>
                ))}
            </div>
        </nav>
    </header>
);

const StockChart = ({ data }) => {
    if (!data || data.length === 0) return <div className="flex items-center justify-center h-64 text-gray-500">No chart data available.</div>;

    const chartData = {
        labels: data.map(d => new Date(d.date).toLocaleTimeString()),
        datasets: [{
            data: data.map(d => d.close),
            borderColor: data[data.length - 1].close >= data[0].close ? '#22c55e' : '#ef4444',
            backgroundColor: 'transparent', pointRadius: 0, tension: 0.1, borderWidth: 2,
        }]
    };
    const options = { responsive: true, maintainAspectRatio: false, plugins: { legend: { display: false } }, scales: { x: { ticks: { display: false } }, y: { ticks: { display: false } } } };

    return <div className="h-64 mt-4"><Line data={chartData} options={options} /></div>;
};

const StockDataCard = ({ data, children }) => {
    if (!data) return null;
    const isPositive = data.change >= 0;
    const changeColor = isPositive ? 'text-green-500' : 'text-red-500';
    const DataRow = ({ label, value }) => (<div className="flex justify-between py-3 border-b"><span className="text-sm text-gray-500">{label}</span><span className="text-sm font-medium">{value}</span></div>);

    return (
        <div className="mt-8 bg-white p-6 rounded-xl shadow-lg animate-fade-in">
            <div className="flex justify-between items-start">
                <div> <h2 className="text-2xl font-bold">{data.companyName} ({data.symbol})</h2> <p className="text-3xl font-bold mt-2">${data.price?.toFixed(2)}</p> </div>
                <div className={`flex items-center text-lg font-semibold ${changeColor}`}> {isPositive ? '▲' : '▼'} <span>{data.change?.toFixed(2)} ({data.changePercent?.toFixed(2)}%)</span> </div>
            </div>
            {children} {/* Renders chart, watchlist button, etc. */}
            <div className="mt-6 space-y-2">
                <DataRow label="Market Cap" value={data.marketCap ? new Intl.NumberFormat('en-US', { notation: 'compact' }).format(data.marketCap) : 'N/A'} />
                <DataRow label="P/E Ratio" value={data.peRatio ? data.peRatio.toFixed(2) : 'N/A'} />
                <DataRow label="52 Week High" value={data.week52High ? `$${data.week52High.toFixed(2)}` : 'N/A'} />
                <DataRow label="52 Week Low" value={data.week52Low ? `$${data.week52Low.toFixed(2)}` : 'N/A'} />
            </div>
        </div>
    );
};

const TradeModal = ({ stock, tradeType, closeModal, refreshData, API_URL }) => {
    const [quantity, setQuantity] = useState(1);
    const [error, setError] = useState('');
    const [isProcessing, setIsProcessing] = useState(false);
    const handleSubmit = async (e) => {
        e.preventDefault(); setIsProcessing(true); setError('');
        try {
            const res = await fetch(`${API_URL}/${tradeType}`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ ticker: stock.symbol, quantity }) });
            const result = await res.json();
            if (!res.ok) throw new Error(result.error);
            await refreshData(); closeModal();
        } catch (err) { setError(err.message); } finally { setIsProcessing(false); }
    };
    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
            <div className="bg-white rounded-lg p-8 w-full max-w-md m-4">
                <h2 className="text-2xl font-bold mb-4 capitalize">{tradeType} {stock.symbol}</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-4"> <label className="block font-bold mb-2">Quantity</label> <input type="number" value={quantity} onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value) || 1))} className="border rounded w-full py-2 px-3" min="1"/> </div>
                    <div className="mb-6"> <p>Est. {tradeType === 'buy' ? 'Cost' : 'Proceeds'}: <span className="font-bold">${(stock.price * quantity).toFixed(2)}</span></p> </div>
                    {error && <p className="text-red-500 mb-4">{error}</p>}
                    <div className="flex justify-end space-x-4"> <button type="button" onClick={closeModal} className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded">Cancel</button> <button type="submit" disabled={isProcessing} className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded disabled:bg-blue-300">Confirm</button> </div>
                </form>
            </div>
        </div>
    );
};

// --- Page Components ---

const SearchPage = ({ API_URL, refreshData, watchlist, setWatchlist }) => {
    const [ticker, setTicker] = useState('');
    const [stockData, setStockData] = useState(null);
    const [chartData, setChartData] = useState([]);
    const [chartPeriod, setChartPeriod] = useState('6mo');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [showTradeModal, setShowTradeModal] = useState(false);
    const [tradeType, setTradeType] = useState('buy');

    const handleSearch = useCallback(async (searchTicker, period) => {
        if (!searchTicker) return;
        setLoading(true); setError('');
        try {
            const [stockRes, chartRes] = await Promise.all([
                fetch(`${API_URL}/stock/${searchTicker}`),
                fetch(`${API_URL}/chart/${searchTicker}?period=${period}`)
            ]);
            if (!stockRes.ok) throw new Error('Stock not found');
            const stock = await stockRes.json();
            const chart = await chartRes.json();
            setStockData(stock);
            if(chart.error) { console.warn(chart.error); setChartData([]); } else { setChartData(chart); }
        } catch (err) { setError(`Could not find data for "${searchTicker}".`); setStockData(null); setChartData([]); } 
        finally { setLoading(false); }
    }, [API_URL]);

    useEffect(() => {
        if (stockData) {
            handleSearch(stockData.symbol, chartPeriod);
        }
    }, [chartPeriod]);

    const isWatched = stockData && watchlist.includes(stockData.symbol);

    const toggleWatchlist = async () => {
        const symbol = stockData.symbol;
        const method = isWatched ? 'DELETE' : 'POST';
        const response = await fetch(`${API_URL}/watchlist/${symbol}`, { method });
        const data = await response.json();
        setWatchlist(data.watchlist);
    };

    return (
        <div className="container mx-auto px-4 py-16 flex flex-col items-center">
            {showTradeModal && <TradeModal stock={stockData} tradeType={tradeType} closeModal={() => setShowTradeModal(false)} refreshData={refreshData} API_URL={API_URL} />}
            <div className="w-full max-w-2xl text-center">
                <h1 className="text-5xl font-extrabold text-gray-800">Stock Ticker Search</h1>
                <form onSubmit={(e) => { e.preventDefault(); handleSearch(ticker, chartPeriod); }} className="mt-8 flex">
                    <input type="text" value={ticker} onChange={(e) => setTicker(e.target.value.toUpperCase())} placeholder="e.g., AAPL" className="w-full px-4 py-4 text-lg border-2 rounded-l-lg focus:ring-2 focus:ring-blue-500 outline-none" />
                    <button type="submit" disabled={loading} className="bg-blue-600 text-white font-bold px-8 py-4 rounded-r-lg hover:bg-blue-700 disabled:bg-blue-300"> {loading ? '...' : 'Search'} </button>
                </form>
            </div>
            <div className="w-full max-w-2xl mt-4">
                {error && <div className="text-red-500 text-center p-4 bg-red-100 rounded-lg">{error}</div>}
                {stockData && (
                    <StockDataCard data={stockData}>
                        <StockChart data={chartData} />
                        <div className="flex justify-center space-x-2 mt-2">
                            {["1d", "5d", "1mo", "6mo", "1y"].map(p => (
                                <button key={p} onClick={() => setChartPeriod(p)} className={`px-3 py-1 text-xs rounded ${chartPeriod === p ? 'bg-blue-600 text-white' : 'bg-gray-200 hover:bg-gray-300'}`}>{p}</button>
                            ))}
                        </div>
                        <button onClick={toggleWatchlist} className={`w-full mt-4 py-3 rounded-lg font-bold text-white transition-colors ${isWatched ? 'bg-yellow-500 hover:bg-yellow-600' : 'bg-blue-500 hover:bg-blue-600'}`}>
                            {isWatched ? 'Remove from Watchlist' : 'Add to Watchlist'}
                        </button>
                        <div className="mt-4 flex space-x-4">
                            <button onClick={() => { setTradeType('buy'); setShowTradeModal(true); }} className="w-full bg-green-500 hover:bg-green-600 text-white font-bold py-3 rounded-lg">Buy</button>
                            <button onClick={() => { setTradeType('sell'); setShowTradeModal(true); }} className="w-full bg-red-500 hover:bg-red-600 text-white font-bold py-3 rounded-lg">Sell</button>
                        </div>
                    </StockDataCard>
                )}
            </div>
        </div>
    );
};

const WatchlistPage = ({ API_URL, watchlist, setWatchlist }) => {
    const [stocks, setStocks] = useState([]);
    useEffect(() => {
        const fetchStockData = async () => {
            const results = await Promise.all(watchlist.map(ticker => fetch(`${API_URL}/stock/${ticker}`).then(res => res.json())));
            setStocks(results.filter(s => !s.error));
        };
        if (watchlist.length > 0) fetchStockData(); else setStocks([]);
    }, [watchlist, API_URL]);

    const removeFromWatchlist = async (ticker) => {
        const res = await fetch(`${API_URL}/watchlist/${ticker}`, { method: 'DELETE' });
        setWatchlist((await res.json()).watchlist);
    };

    return (
        <div className="container mx-auto px-4 py-8">
            <h1 className="text-4xl font-extrabold text-center mb-8">Your Watchlist</h1>
            {watchlist.length === 0 ? (<p className="text-center text-gray-500">Your watchlist is empty.</p>) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {stocks.map(stock => (
                        <div key={stock.symbol} className="bg-white p-6 rounded-xl shadow-lg">
                            <div className="flex justify-between items-center">
                                <h2 className="text-xl font-bold">{stock.symbol}</h2>
                                <button onClick={() => removeFromWatchlist(stock.symbol)} className="text-red-500 hover:text-red-700 text-sm">Remove</button>
                            </div>
                            <p className="text-sm text-gray-500 truncate">{stock.companyName}</p>
                            <p className="text-2xl font-bold mt-2">${stock.price?.toFixed(2)}</p>
                            <p className={`font-semibold ${stock.change >= 0 ? 'text-green-500' : 'text-red-500'}`}>{stock.change?.toFixed(2)} ({stock.changePercent?.toFixed(2)}%)</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

const PortfolioPage = ({ portfolioData, isLoading, API_URL }) => {
    const [selectedStock, setSelectedStock] = useState(null);
    const [chartData, setChartData] = useState([]);
    const [chartPeriod, setChartPeriod] = useState('6mo');

    useEffect(() => {
        const fetchChart = async () => {
            if (!selectedStock) return;
            const res = await fetch(`${API_URL}/chart/${selectedStock.symbol}?period=${chartPeriod}`);
            const data = await res.json();
            if (data.error) setChartData([]);
            else setChartData(data);
        };
        fetchChart();
    }, [selectedStock, chartPeriod, API_URL]);

    if (isLoading) return <div className="text-center py-10">Loading portfolio...</div>;

    const { cash, stocks } = portfolioData;
    const totalStockValue = stocks.reduce((acc, s) => acc + (s.quantity * s.current_price), 0);
    const portfolioValue = cash + totalStockValue;
    const totalDayPL = stocks.reduce((acc, s) => acc + ((s.current_price - s.previous_close) * s.quantity), 0);
    const totalAllTimePL = stocks.reduce((acc, s) => acc + ((s.current_price - s.avg_price) * s.quantity), 0);

    return (
        <div className="container mx-auto px-4 py-8">
            <h1 className="text-4xl font-extrabold text-center mb-8">Your Portfolio</h1>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-8">
                <SummaryCard title="Total Value" value={portfolioValue} />
                <SummaryCard title="Day's P/L" value={totalDayPL} isPL={true} />
                <SummaryCard title="Available Cash" value={cash} />
            </div>

            {selectedStock && (
                 <div className="bg-white rounded-xl shadow-lg p-6 mb-8">
                    <h3 className="text-2xl font-bold">Chart for {selectedStock.symbol}</h3>
                    <StockChart data={chartData} />
                    <div className="flex justify-center space-x-2 mt-2">
                        {["1d", "5d", "1mo", "6mo", "1y"].map(p => (
                            <button key={p} onClick={() => setChartPeriod(p)} className={`px-3 py-1 text-xs rounded ${chartPeriod === p ? 'bg-blue-600 text-white' : 'bg-gray-200 hover:bg-gray-300'}`}>{p}</button>
                        ))}
                    </div>
                </div>
            )}

            <div className="bg-white rounded-xl shadow-lg overflow-hidden">
                <h3 className="text-2xl font-bold p-6 border-b">Your Holdings</h3>
                <div className="overflow-x-auto">
                    <table className="w-full text-left">
                        <thead className="bg-gray-50"><tr>
                            {['Symbol', 'Quantity', 'Avg Cost', 'Current Price', "Day's P/L", 'Total P/L'].map(h => <th key={h} className="px-6 py-3 text-xs uppercase">{h}</th>)}
                        </tr></thead>
                        <tbody className="divide-y">
                            {stocks.length > 0 ? stocks.map(s => {
                                const dayPL = (s.current_price - s.previous_close) * s.quantity;
                                const totalPL = (s.current_price - s.avg_price) * s.quantity;
                                return (
                                <tr key={s.symbol} onClick={() => setSelectedStock(s)} className="hover:bg-gray-100 cursor-pointer">
                                    <td className="px-6 py-4 font-bold text-blue-600">{s.symbol}</td>
                                    <td className="px-6 py-4">{s.quantity}</td>
                                    <td className="px-6 py-4">${s.avg_price.toFixed(2)}</td>
                                    <td className="px-6 py-4">${s.current_price.toFixed(2)}</td>
                                    <td className={`px-6 py-4 font-semibold ${dayPL >= 0 ? 'text-green-500' : 'text-red-500'}`}>${dayPL.toFixed(2)}</td>
                                    <td className={`px-6 py-4 font-semibold ${totalPL >= 0 ? 'text-green-500' : 'text-red-500'}`}>${totalPL.toFixed(2)}</td>
                                </tr>
                            )}) : ( <tr><td colSpan="6" className="text-center py-10 text-gray-500">No stocks owned.</td></tr> )}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

const SummaryCard = ({ title, value, isPL = false }) => {
    const displayValue = value.toLocaleString('en-US', { style: 'currency', currency: 'USD' });
    let colorClass = 'text-gray-800';
    if (isPL) {
        if (value > 0) colorClass = 'text-green-500';
        if (value < 0) colorClass = 'text-red-500';
    } else if (title === 'Available Cash') {
        colorClass = 'text-blue-600';
    }
    return (
        <div className="bg-white p-6 rounded-xl shadow-lg text-center">
            <h3 className="text-lg font-semibold text-gray-500">{title}</h3>
            <p className={`text-4xl font-bold mt-2 ${colorClass}`}>{displayValue}</p>
        </div>
    );
};

